let pulseCount = context.get('pulseCount') || 0;
let totalLiters = context.get('totalLiters') || 0;
let volumeliters = flow.get('volumeliters') || 0;
// let volumeliters = msg.payload.nilaiWaterflow || 0;
let last_volumeliters = 0;
const calibrationFactor = 1; // Ganti dengan faktor kalibrasi sensor Anda

// Menghitung aliran dan total liter setiap detik
if (msg.payload === 1) { // Sensor mengirimkan pulsa (misalnya, 1) setiap kali terjadi
    pulseCount++;
}

let sendPulse = pulseCount;

const duration = 1.0; // durasi dalam detik
let flowRate = (pulseCount / calibrationFactor); // aliran dalam liter per detik
if (msg.payload.status === 'send') {
    // totalLiters = msg.payload.nilaiWaterflow;
    volumeliters = msg.payload.nilaiWaterflow;
    // totalLiters = volumeliters;
    last_volumeliters = volumeliters;
}
else {
    totalLiters += flowRate * duration; // hitung total liter yang sudah mengalir
    // volumeliters += totalLiters;
    // volumeliters = volumeliters * 6;
    volumeliters += 6.647 * sendPulse + 9.328;
    // volumeliters += 2 * sendPulse;
    last_volumeliters = volumeliters;
}


pulseCount = 0; // reset pulse count

// Simpan nilai di context
context.set('pulseCount', pulseCount);
context.set('totalLiters', totalLiters);
flow.set('volumeliters', volumeliters);

msg.payload = {
    flowRate: flowRate.toFixed(2),
    totalLiters: totalLiters.toFixed(2),
    volumeliters: parseInt(last_volumeliters.toFixed(0)),
    // volumeliters: sendPulse
};

return msg;


let pin_IR_ATAS = 18;
let pin_IR_BAWAH = 23;
let pin_Relay = 8;
let count = 0;
let timer = msg.payload * 1000;
let counting = false;
let prevCount = 0;
let state_IR_ATAS = 0;
let state_IR_BAWAH = 0;

const IR_ATAS = new onoff.Gpio(pin_IR_ATAS, 'in', 'both');
const IR_BAWAH = new onoff.Gpio(pin_IR_BAWAH, 'in', 'both');
const Relay = new onoff.Gpio(pin_Relay, 'out');

state_IR_ATAS = IR_ATAS.readSync();
state_IR_BAWAH = IR_BAWAH.readSync();
prevCount = Date.now();
let stopTimer = Date.now();
count = 0;

while (count < timer && Date.now()-stopTimer < 5000) {
    state_IR_ATAS = IR_ATAS.readSync();
    state_IR_BAWAH = IR_BAWAH.readSync();
    if (state_IR_ATAS === 1 || state_IR_BAWAH === 1) {
        if (!counting) {
            counting = true;
            prevCount = Date.now() - count;
        }
        stopTimer = Date.now();
        count = Date.now() - prevCount;
        Relay.writeSync(1);
    }
    else {
        if (counting) {
            counting = false;
        }
        Relay.writeSync(0);
    }
    // setTimeout(() => {
    //     count += 10;
    // }, 10);
}

Relay.writeSync(0);

const d = new Date();

msg.payload = {
    finish: 'done',
    time: d.getHours() + ":" + d.getMinutes() + ":" + d.getSeconds(),
    date: d.getFullYear() + "-" + d.getMonth() + "-" + d.getDate(),
    volume: 250,
    temperature: 'hot'
}


return msg

